async function runSmokeTest() {
  const baseUrl = 'https://vbank.open.bankingapi.ru';
  const clientId = 'team134';
  const clientSecret = '9rloz5k3b9CeHlzvx3WaNwT0tpUw09B4';

  try {
    // Step 1: Authentication
    const authResponse = await fetch(`${baseUrl}/auth/bank-token`, {
      method: 'POST',
      headers: { 'Content-Type': 'application/json' },
      body: JSON.stringify({ client_id: clientId, client_secret: clientSecret })
    });
    if (!authResponse.ok) throw new Error('Authentication failed');
    const { access_token } = await authResponse.json();
    console.log('✅ Authentication successful');

    // Step 2: Get accounts
    const accountsResponse = await fetch(`${baseUrl}/accounts`, {
      headers: { Authorization: `Bearer ${access_token}` }
    });
    if (!accountsResponse.ok) throw new Error('Failed to get accounts');
    const accounts = await accountsResponse.json();
    console.log('✅ Accounts retrieved');

    // Step 3: Check bonus account balance
    const bonusAccount = accounts.find(acc => acc.type === 'bonus');
    if (!bonusAccount) throw new Error('Bonus account not found');
    const balanceResponse = await fetch(`${baseUrl}/accounts/${bonusAccount.id}/balances`, {
      headers: { Authorization: `Bearer ${access_token}` }
    });
    if (!balanceResponse.ok) throw new Error('Failed to get balance');
    console.log('✅ Bonus balance checked');

    // Step 4: Initiate payment
    const paymentResponse = await fetch(`${baseUrl}/payments`, {
      method: 'POST',
      headers: {
        'Authorization': `Bearer ${access_token}`,
        'Content-Type': 'application/json'
      },
      body: JSON.stringify({
        account_id: bonusAccount.id,
        amount: 100,
        description: 'Test bonus payment'
      })
    });
    if (!paymentResponse.ok) throw new Error('Payment initiation failed');
    console.log('✅ Payment initiated successfully');

  } catch (error) {
    console.error('❌ Smoke test failed:', error.message);
  }
}

runSmokeTest();

